<?php// 文章模型控制器class LoginController extends BaseController{          public function  indexAction()          {              include CUR_VIEW_PATH . "Slogin" . DS . "login_index.html";          }          public function loginAction(){              $model=new ModelNew('user');              $yonghuming=$_POST['yonghuming'];              $rs=$model->where(['yonghuming'=>$yonghuming])->find()->one();              if ($rs){                  $mima=md5($_POST['mima']);                  if ($rs['mima']==$mima){                      if ($rs['shield']=='是'){                          $this->jump('index.php?p=show&c=login&a=index','该账号已被禁用',3);                      }else{                          $_SESSION['id'] = $rs['id'];                          $_SESSION['touxiang']=$rs['touxiang'];                          $_SESSION['mingcheng']=$rs['xingming'];                          $_SESSION['yonghuming']=$rs['yonghuming'];                          $this->jump('index.php?p=show&c=admin&a=index','登录成功',3);                      }                  }else{                      $this->jump('index.php?p=show&c=login&a=index','密码错误',3);                  }              }else{                  $this->jump('index.php?p=show&c=login&a=index','账号不存在',3);              }          }}